import React, {useMemo, useState} from 'react';
import Post from "./post";
import PostForm from "./PostForm";
import MySelect from "../UI/selects/MySelect";
import MyInput from "../UI/inputs/MyInput";

const PostList = (props) => {
    let [posts, setPosts] = useState([]);
    let [selectedSort, SetSelectedSort] = useState('');
    let [searchQuery, setSearchQuery] = useState('');




    let sortedPosts = useMemo(() => {
        console.log('1')
        console.log(selectedSort)
        if(selectedSort){
            return [...posts].sort((a, b) => a[selectedSort].localeCompare(b[selectedSort]))
        }
        return posts;
    }, [selectedSort, posts]);


    function createPost(newPost){
        setPosts([...posts, newPost]);
    }
    function deletePost(post){
        setPosts(posts.filter(p => p.id !== post.id));
    }

    function sortPosts(sort){
        SetSelectedSort(sort);
    }
    return (
        <div>
            <h1>{props.title}</h1>
            <PostForm create = {createPost}/>
            <hr style={{margin: '20px 0'}}/>
            <MyInput
                value={searchQuery}
                onChange={el => setSearchQuery(el.target.value)}
                placeholder='Поиск'
            />
            <MySelect defaultValue='Сортировка'
            value={selectedSort}
            onChange={sortPosts}
            options = {[{
                value: 'title', name: 'Заголовок'
            }, {
                value: 'body', name: 'Описание'
            }]}
            />
            {(posts.length == 0) ?
                <h1>Постов нет</h1>
                :
                posts.map(post => <Post title={post.title} description={post.body} id={post.id} key={post.key} deletePost = {deletePost}/>)}
        </div>
    );
};

export default PostList;
