import React, {useState} from 'react';
import Post from "./post";
import PostForm from "./PostForm";
import MySelect from "../UI/selects/MySelect";

const PostList = (props) => {
    let [posts, setPosts] = useState([])
    let [selectedSort, SetSelectedSort] = useState('')

    function createPost(newPost){
        setPosts([...posts, newPost]);
    }

    function deletePost(post){
        setPosts(posts.filter(p => p.id !== post.id));
    }

    function sortPosts(sort){
        console.log(sort)
        setPosts([...posts].sort((a, b) => a[sort].localeCompare(b[sort])))
    }
    return (
        <div>
            <h1>{props.title}</h1>
            <PostForm create = {createPost}/>
            <hr style={{margin: '20px 0'}}/>
            <MySelect defaultValue='Сортировка'
            value={selectedSort}
            onChange={sortPosts}
            options = {[{
                value: 'title', name: 'Заголовок'
            }, {
                value: 'body', name: 'Описание'
            }]}
            />
            {posts.map(post => <Post title={post.title} description={post.body} id={post.id} key={post.key} deletePost = {deletePost}/>)}
        </div>
    );
};

export default PostList;
