import React, {useState} from 'react';
import Post from "./post";
import MyInput from "../UI/inputs/MyInput";
import MyButton from "../UI/buttons/MyButton";

const PostList = (props) => {
    let [posts, setPost] = useState([
        {title: 'header1', description: 'desc1', id: 1},
        {title: 'header2', description: 'desc2', id: 2}
    ])


    let [postTitle, setPostTitle] = useState('');
    let [postDesc, setPostDesc] = useState('');
    function addPost(e){
        e.preventDefault();
        setPost([...posts,{
            title: postTitle,
            description: postDesc,
            id: posts[posts.length - 1].id + 1,
        }]);
    console.log(posts)
        setPostDesc('');
        setPostTitle('');
    }

    return (
        <div>
            <h1>{props.title}</h1>

            <form action="" >
                <MyInput value={postTitle}
                         onChange = {(e) => setPostTitle(e.target.value)}
                         placeholder = "Введите название поста"/>
                <MyInput value={postDesc}
                         onChange = {(e) => setPostDesc(e.target.value)}
                         placeholder = "Введите описание"/>
                <MyButton  onClick = {addPost}>Отправить</MyButton>
            </form>
            {posts.map(post => <Post title={post.title} description={post.description} key={post.id}/>)}
        </div>
    );
};

export default PostList;
